DROP TABLE IF EXISTS `user`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
 SET character_set_client = utf8mb4 ;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户ID',
  `username` varchar(32) DEFAULT NULL COMMENT '用户名',
  `password` varchar(255) DEFAULT NULL COMMENT '密码',
  `icon` varchar(500) DEFAULT 'http://localhost:8082/static/info.png' COMMENT '头像',
  `money` double DEFAULT '0' COMMENT '余额',
  `place` varchar(50) DEFAULT NULL COMMENT '地区',
  `age` int(11) DEFAULT '0',
  `sex` varchar(10) DEFAULT NULL COMMENT '性别',
  `birthday` varchar(20) DEFAULT NULL COMMENT '生日',
  `phone` varchar(20) DEFAULT NULL COMMENT '手机',
  `email` varchar(20) DEFAULT NULL COMMENT '邮箱',
  `is_doctor` int(11) NOT NULL DEFAULT '0' COMMENT '0表示不是医生。其他表示是医生，并且和相应医生id关联',
  `is_admin` int(3) DEFAULT '0' COMMENT '0为非管理员，1为管理员',
